import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { BASE_URL } from "../utils/config.js";

const ManageTours = () => {
  const [tours, setTours] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTours = async () => {
      try {
        const res = await axios.get(`${BASE_URL}/tours`);
        setTours(res.data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };
    fetchTours();
  }, []);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  return (
    <div>
      <h2>Gestionar Tours</h2>
      <table>
        <thead>
          <tr>
            <th>TÃ­tulo</th>
            <th>Ciudad</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {tours.map((tour) => (
            <tr key={tour._id}>
              <td>{tour.title}</td>
              <td>{tour.city}</td>
              <td>
                <Link to={`/edit-tour/${tour._id}`}>Editar</Link>
                <Link to={`/delete-tour/${tour._id}`}>Eliminar</Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <Link to="/create-tour">Crear nuevo tour</Link>
    </div>
  );
};

export default ManageTours;
