import React, { useState, useContext } from "react";
import "./booking.css";
import {
    Form,
    FormGroup,
    ListGroup,
    ListGroupItem,
    Button,
    Modal,
    ModalBody,
} from "reactstrap";

import { Link } from 'react-router-dom';

import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import { BASE_URL } from "../../utils/config";

const Booking = ({ tour, avgRating }) => {
    const { price, reviews, title } = tour;
    const navigate = useNavigate();

    const { user } = useContext(AuthContext);

    const [isModalOpen, setIsModalOpen] = useState(false);

    const [credentials, setCredentials] = useState({
        email: "",
        password: "",
    });

    const [booking, setBooking] = useState({
        userId: user && user._id,
        userEmail: user && user.email,
        tourName: title,
        fulName: "",
        phone: "",
        guestSize: 1,
        booAt: "",
    });

    const handleChange = (e) => {
        setBooking((prev) => ({ ...prev, [e.target.id]: e.target.value }));
    };

    const serviceFee = 10;
    const totalAmount =
        Number(price) * Number(booking.guestSize) + Number(serviceFee);


    const handleOpenModal = () => {
        setIsModalOpen(true);
    };

    //send data to the server

    const handleClik = async (e) => {
        e.preventDefault();

        console.log(booking);

        try {
            if (!user || user === undefined || user === null) {
                return handleOpenModal();
            }

            // ...
        } catch (err) {
            alert(err.message);
        }
    };

    const handleLogin = async (e) => {
        e.preventDefault();

        try {
            const res = await fetch(`${BASE_URL}/auth/login`, {
                method: "post",
                headers: {
                    "content-type": "application/json",
                },
                credentials: "include",
                body: JSON.stringify(credentials),
            });

            const result = await res.json();

            if (!res.ok) {
                return alert(result.message);
            }

            setIsModalOpen(false);
            navigate("/thank-you");
        } catch (err) {
            alert(err.message);
        }
    };


    function BookingForm() {
        const [formData, setFormData] = useState({});
        const [isFormValid, setIsFormValid] = useState(false);
      
        const handleChange = (event) => {
          const { id, value } = event.target;
          setFormData((prevState) => ({
            ...prevState,
            [id]: value
          }));
          validateForm();
        };
      
        const validateForm = () => {
          const requiredFields = ["fullName", "phone", "bookAt", "guestSize"];
          const hasEmptyField = requiredFields.some((field) => !formData[field]);
          setIsFormValid(!hasEmptyField);
        };
      
        const handleClick = (event) => {
          if (!isFormValid) {
            event.preventDefault();
          } else {
            // Submit the form or do something else with the data
          }
        };
    };



    return (
        <div className="booking">
            <div className="booking__top d-flex align-items-center justify-content-between">
                <h3>
                    S/.{price} <span>/por persona</span>
                </h3>

                <span className="tour__rating d-flex align-items-center">
                    <i class="ri-star-s-fill"></i>
                    {avgRating === 0 ? null : avgRating} ({reviews?.length})
                </span>
            </div>

            {/*============== booking form ====================*/}
            <div className="booking__form">
                <h5>Informacion</h5>
                <Form className="booking__info-form" onSubmit={handleClik}>
                    <FormGroup>
                        <input
                            type="text"
                            placeholder="Nombre Completo"
                            id="fullName"
                            required
                            onChange={handleChange}
                        />
                    </FormGroup>
                    <FormGroup>
                        <input
                            type="number"
                            placeholder="Telefono"
                            id="phone"
                            required
                            onChange={handleChange}
                        />
                    </FormGroup>
                    <FormGroup className="d-flex align-items-center gap-3">
                        <input
                            type="date"
                            placeholder=""
                            id="bookAt"
                            required
                            onChange={handleChange}
                        />
                        <input
                            type="number"
                            placeholder="Guest"
                            id="guestSize"
                            required
                            onChange={handleChange}
                        />
                    </FormGroup>
                </Form>
            </div>
            {/*============== booking end ====================*/}


            {/*============== booking botton ====================*/}
            <div className="booking__botton">
                <ListGroup>
                    <ListGroupItem className="border-0 px-0">
                        <h5 className="d-flex align-items-center gap-1">
                            S/.{price} <i class="ri-close-line"></i> 1 persona
                        </h5>
                        <span>S/.{price}</span>
                    </ListGroupItem>
                </ListGroup>
                <ListGroup>
                    <ListGroupItem className="border-0 px-0">
                        <h5>Cargo por servicio</h5>
                        <span>S/.{serviceFee}</span>
                    </ListGroupItem>
                </ListGroup>
                <ListGroup>
                    <ListGroupItem className="border-0 px-0 total">
                        <h5>Total</h5>
                        <span>S/.{totalAmount}</span>
                    </ListGroupItem>
                </ListGroup>

                <Button className="btn primary__btn w-100 mt-4" onClick={handleOpenModal}>
                    Pagar
                </Button>
            </div>

            {/*============== booking botton  end ====================*/}


            {/*============== Modal ====================*/}

            <Modal isOpen={isModalOpen} toggle={() => setIsModalOpen(false)} className="modal__form">
                <ModalBody>
                        <h2>Login</h2>
                        <Form>
                            <FormGroup>
                                <input type="email" placeholder="Email" required id="email"
                                    onChange={(e) =>
                                        setCredentials({
                                            ...credentials,
                                            email: e.target.value,
                                        })
                                    }
                                />
                            </FormGroup>
                            <FormGroup>
                                <input type="password" placeholder="Password" required id="password"
                                    onChange={(e) =>
                                        setCredentials({
                                            ...credentials,
                                            password: e.target.value,
                                        })
                                    }
                                />
                            </FormGroup>
                            <Button className="btn primary__btn w-100 mt-4" onClick={handleLogin}>
                                Iniciar sesi√≥n
                            </Button>
                        </Form>
                        <p>Don't have an accouunt? <Link to='/register'>Create</Link></p>
                </ModalBody>
            </Modal>

            {/*============== Modal end ====================*/}

        </div>
    );
};

export default Booking;
