import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useHistory } from 'react-router-dom';
import { toast } from 'react-toastify';
import { BASE_URL } from '../../utils/config';

const DeleteContact = () => {
  const [contact, setContact] = useState(null);
  const { id } = useParams();
  const history = useHistory();

  useEffect(() => {
    const fetchContact = async () => {
      try {
        const res = await axios.get(`${BASE_URL}/contact/${id}`);
        setContact(res.data);
      } catch (error) {
        toast.error("Error al obtener el contacto");
      }
    };

    fetchContact();
  }, [id]);

  const handleDelete = async () => {
    try {
      await axios.delete(`${BASE_URL}/contact/${id}`);
      toast.success("Contacto eliminado correctamente");
      history.push('/manage_contacts');
    } catch (error) {
      toast.error("Error al eliminar el contacto");
    }
  };

  return (
    <div>
      <h2>Eliminar Contacto</h2>
      {contact ? (
        <div>
          <p>¿Estás seguro de que quieres eliminar el siguiente contacto?</p>
          <p>
            <strong>Nombre:</strong> {contact.name}
          </p>
          <p>
            <strong>Email:</strong> {contact.email}
          </p>
          <p>
            <strong>Mensaje:</strong> {contact.message}
          </p>
          <button onClick={handleDelete} className="btn danger__btn">
            Eliminar
          </button>
        </div>
      ) : (
        <p>Cargando...</p>
      )}
    </div>
  );
};

export default DeleteContact;
