import React, { useEffect, useRef, useState, useContext } from "react";
import "../styles/tour-details.css";
import { Container, Row, Col, Form, ListGroup } from "reactstrap";
import { useParams } from "react-router-dom";
import calculateAvgRating from "./../utils/avgRating";
import avatar from "../assets/images/avatar.jpg";
import Booking from "../components/Booking/Booking";
import Newsletter from "./../shared/Newsletter";
import useFetch from "./../hooks/useFetch";
import { BASE_URL } from "./../utils/config";
import { AuthContext } from "./../context/AuthContext";
import LoginModal from "../components/Login-Modal/LoginModal.jsx";
import { Oval } from "react-loader-spinner";
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const PAGE_SIZE = 8; // Número de reviews por página

const TourDetails = () => {
  const { id } = useParams();
  const reviewMsgRef = useRef("");
  const { user } = useContext(AuthContext);

  const { data: tour, loading, error } = useFetch(`${BASE_URL}/tours/${id}`);
  const [reviews, setReviews] = useState([]);
  const [totalReviews, setTotalReviews] = useState(0);
  const [selectedRating, setSelectedRating] = useState(null);
  const [pageCount, setPageCount] = useState(0);
  const [currentPage, setCurrentPage] = useState(0);

  useEffect(() => {
    if (tour) {
      setReviews(tour.reviews);
      setTotalReviews(tour.reviews.length);
      const pages = Math.ceil(tour.reviews.length / PAGE_SIZE);
      setPageCount(pages);
    }
  }, [tour]);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const submitHandler = async (e) => {
    e.preventDefault();
    const reviewText = reviewMsgRef.current.value;

    try {
      if (!user || user === undefined || user === null) {
        return handleOpenModal();
      }

      const reviewObj = {
        username: user?.username,
        reviewText,
        rating: selectedRating,
      };

      const res = await fetch(`${BASE_URL}/review/${id}`, {
        method: "post",
        headers: {
          "content-type": "application/json",
        },
        credentials: "include",
        body: JSON.stringify(reviewObj),
      });

      const result = await res.json();
      if (!res.ok) {
        return toast.error(result.message);
      }

      window.location.reload();

    } catch (err) {
      toast.error(err.message);
    }
  };

  return (
    <>
      <section>
        <Container>
          {loading && (
            <h4 className="text-center pt-5">
              <Oval
                color="#f11c00"
                height={100}
                width={100}
                timeout={3000} //3 secs
              />
            </h4>
          )}
          {error && <h4 className="text-center pt-5">{error}</h4>}
          {!loading && !error && (
            <Row>
              <Col lg="8">
                <div className="tour__content">
                  <img src={tour.photo} alt="" />

                  <div className="tour__info">
                    <h2>{tour.title}</h2>

                    <div className="d-flex align-items-center gap-5">
                      <span className="tour__rating d-flex align-items-center gap-1">
                        <i
                          className="ri-star-s-fill"
                          style={{ color: "var(--secondary-color)" }}
                        ></i>
                        {tour.avgRating === 0 ? null : tour.avgRating}
                        {tour.totalRating === 0 ? (
                          "Not rated"
                        ) : (
                          <span>({tour.reviews?.length})</span>
                        )}
                      </span>
                      <span>
                        <i className="ri-map-pin-user-fill"></i> {tour.address}
                      </span>
                    </div>
                    <div className="tour__extra-details">
                      <span>
                        <i className="ri-map-pin-2-line"></i> {tour.city}
                      </span>
                      <span>
                        <i className="ri-money-dollar-circle-line"></i> S/.{tour.price}{" "}
                        /por person
                      </span>
                      <span>
                        <i className="ri-map-pin-time-line"></i> {tour.distance} k/m
                      </span>
                      <span>
                        <i className="ri-group-line"></i> {tour.maxGroupSize} personas
                      </span>
                    </div>
                    <h5>Descripcion</h5>
                    <p>{tour.desc}</p>
                  </div>

                  {/* ========== tour reviews section =========== */}
                  <div className="tour__reviews mt-4">
                    <h4>Reviews ({totalReviews} reviews)</h4>

                    <Form onSubmit={submitHandler}>
                      <div className="d-flex align-items-center gap-3 mb-4 rating__group">
                        <span onClick={() => setSelectedRating(1)}>1
                          <i
                            className="ri-star-s-fill"
                            style={{
                              color: selectedRating >= 1 ? "var(--secondary-color)" : "black",
                            }}
                          ></i>
                        </span>
                        <span onClick={() => setSelectedRating(2)}>2
                          <i
                            className="ri-star-s-fill"
                            style={{
                              color: selectedRating >= 2 ? "var(--secondary-color)" : "black",
                            }}
                          ></i>
                        </span>
                        <span onClick={() => setSelectedRating(3)}>3
                          <i
                            className="ri-star-s-fill"
                            style={{
                              color: selectedRating >= 3 ? "var(--secondary-color)" : "black",
                            }}
                          ></i>
                        </span>
                        <span onClick={() => setSelectedRating(4)}>4
                          <i
                            className="ri-star-s-fill"
                            style={{
                              color: selectedRating >= 4 ? "var(--secondary-color)" : "black",
                            }}
                          ></i>
                        </span>
                        <span onClick={() => setSelectedRating(5)}>5
                          <i
                            className="ri-star-s-fill"
                            style={{
                              color: selectedRating >= 5 ? "var(--secondary-color)" : "black",
                            }}
                          ></i>
                        </span>
                      </div>

                      <div className="review__input">
                        <input
                          type="text"
                          ref={reviewMsgRef}
                          placeholder="Danos tu opinion"
                          required
                        />
                        <button
                          className="btn primary__btn text-white"
                          type="submit"
                        >
                          Enviar
                        </button>
                      </div>
                    </Form>

                    <ListGroup className="user__reviews">
                      {reviews
                        .slice(currentPage * PAGE_SIZE, (currentPage + 1) * PAGE_SIZE)
                        .map((review, index) => (
                          <div className="review__item" key={index}>
                            <img src={avatar} alt="" />

                            <div className="w-100">
                              <div className="d-flex align-items-center justify-content-between">
                                <div>
                                  <h5>{review.username}</h5>
                                  <p>
                                    {new Date(
                                      review.createdAt
                                    ).toLocaleDateString("es-PE", options)}
                                  </p>
                                </div>
                                <span className="d-flex align-items-center">
                                  {review.rating}
                                  <i className="ri-star-s-fill"></i>
                                </span>
                              </div>

                              <h6>{review.reviewText}</h6>
                            </div>
                          </div>
                        ))}
                    </ListGroup>

                    {pageCount > 1 && (
                      <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                        {[...Array(pageCount).keys()].map((number) => (
                          <span
                            key={number}
                            onClick={() => handlePageChange(number)}
                            className={currentPage === number ? "active__page" : ""}
                          >
                            {number + 1}
                          </span>
                        ))}
                      </div>
                    )}
                  </div>
                  {/* ========== tour reviews section end =========== */}
                </div>
              </Col>
              <Col lg="4">
                <Booking tour={tour} avgRating={tour.avgRating} />
              </Col>
            </Row>
          )}
        </Container>
      </section>
      {/*============== Modal ====================*/}
      <LoginModal
        isModalOpen={isModalOpen}
        toggleModal={toggleModal}
      />
      {/*============== Modal end ====================*/}
      <Newsletter />
    </>
  );
};

export default TourDetails;
