import React, { useState, useContext, useEffect } from "react";
import "./booking.css";
import Payment from "../Payment/Payment.jsx";
import {
    Form,
    FormGroup,
    ListGroup,
    ListGroupItem,
} from "reactstrap";
import LoginModal from "../Login-Modal/LoginModal.jsx";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import { BASE_URL } from "../../utils/config";

import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import axios from 'axios';

const Booking = ({ tour, avgRating }) => {
    const { price, reviews, title } = tour;
    const navigate = useNavigate();

    const { user } = useContext(AuthContext);

    const [booking, setBooking] = useState({
        userId: user && user._id,
        userEmail: user && user.email,
        tourName: title,
        phone: "",
        guestSize: 1,
        booAt: "",
    });

    const serviceFee = 10;
    const totalAmount =
        Number(price) * Number(booking.guestSize) + Number(serviceFee);

    const { dispatch } = useContext(AuthContext);

    const [credentials, setCredentials] = useState({
        email: undefined,
        password: undefined,
    });

    const quantity = booking.guestSize;

    const handleOpenModal = () => {
        setIsModalOpen(true);
    };

    const currentURL = window.location.pathname;
    const [isModalOpen, setIsModalOpen] = useState(false);
    const toggleModal = () => setIsModalOpen(!isModalOpen);

     // DNI y datos del usuario
     const [dni, setDni] = useState(new Array(quantity).fill(""));
     const [userData, setUserData] = useState(new Array(quantity).fill({}));
 

    const handleChange = (e) => {
        setBooking((prev) => ({ ...prev, [e.target.id]: e.target.value }));
    };


     // Llamada a la API de RENIEC
     useEffect(() => {
        const fetchDniData = async () => {
            let tempUserData = [...userData];
            for(let i=0; i<quantity; i++){
                if (dni[i].length === 8) {
                    try {
                        const response = await axios.get(`https://dniruc.apisperu.com/api/v1/dni/${dni[i]}?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJlbWFpbCI6ImtldmluZmdlMjQxMEBnbWFpbC5jb20ifQ.Ha6IN3AcX5zAC1pBssILUz1U8lg03rodOkh2rpxjKag`);
        
                        tempUserData[i] = response.data;
                    } catch (error) {
                        console.error("Error al obtener los datos del DNI: ", error);
                    }
                }
            }
            setUserData(tempUserData);
        };

        fetchDniData();
    }, [dni, quantity]);

    const handleLogin = async (e) => {
        e.preventDefault();
    
        try {
            const res = await fetch(`${BASE_URL}/auth/login`, {
                method: "post",
                headers: {
                    "content-type": "application/json",
                },
                credentials: "include",
                body: JSON.stringify(credentials),
            });
    
            const result = await res.json();
    
            if (!res.ok) toast.error(result.message);
    
            console.log(result.data);
    
            dispatch({ type: "LOGIN_SUCCESS", payload: result.data });
            setIsModalOpen(false); // Cierra el modal antes de redirigir al usuario
            navigate(currentURL); // Redirige al usuario a la p치gina del tour actual en lugar de la p치gina de inicio
        } catch (err) {
            dispatch({ type: "LOGIN_FAILURE", payload: err.message });
        }
    };
    

    return (
        <div className="booking">
            <div className="booking__top d-flex align-items-center justify-content-between">
                <h3>
                    S/.{price} <span>/por persona</span>
                </h3>

                <span className="tour__rating d-flex align-items-center">
                    <i className="ri-star-s-fill"></i>
                    {avgRating === 0 ? null : avgRating} ({reviews?.length})
                </span>
            </div>

            {/*============== booking form ====================*/}
            <div className="booking__form">
                <h5>Informacion</h5>
                <Form className="booking__info-form" onSubmit={(e) => e.preventDefault()}>
                    <FormGroup>
                    <input
                            type="number"
                            placeholder="Guest"
                            id="guestSize"
                            required
                            onChange={handleChange}
                        />
                    </FormGroup>
                    <FormGroup>
                        <input
                            type="number"
                            placeholder="Telefono"
                            id="phone"
                            required
                            onChange={handleChange}
                        />
                    </FormGroup>
                    <FormGroup className="d-flex align-items-center gap-3">
                        <input
                            type="date"
                            placeholder=""
                            id="bookAt"
                            required
                            onChange={handleChange}
                        />
                    </FormGroup>
                    {/*Agregar campos de DNI*/}
                {[...Array(quantity)].map((_, i) => (
                    <FormGroup key={i}>
                        <input
                            type="number"
                            placeholder="DNI"
                            value={dni[i]}
                            onChange={(e) => {
                                let tempDni = [...dni];
                                tempDni[i] = e.target.value;
                                setDni(tempDni);
                            }}
                        />
                        <button onClick={() => {
                            let tempDni = [...dni];
                            tempDni[i] = "";
                            setDni(tempDni);
                            let tempUserData = [...userData];
                            tempUserData[i] = {};
                            setUserData(tempUserData);
                        }}>
                            Validar
                        </button>
                        {userData[i].nombres && <p>Nombres: {userData[i].nombres}</p>}
                        {userData[i].apellidos && <p>Apellidos: {userData[i].apellidos}</p>}
                    </FormGroup>
                ))}
                </Form>
            </div>
            {/*============== booking end ====================*/}

            {/*============== booking botton ====================*/}
            <div className="booking__botton">
                <ListGroup>
                    <ListGroupItem className="border-0 px-0">
                        <h5 className="d-flex align-items-center gap-1">
                            S/.{price} <i className="ri-close-line"></i> 1 persona
                        </h5>
                        <span>S/.{price}</span>
                    </ListGroupItem>
                </ListGroup>
                <ListGroup>
                    <ListGroupItem className="border-0 px-0">
                        <h5>Cargo por servicio</h5>
                        <span>S/.{serviceFee}</span>
                    </ListGroupItem>
                </ListGroup>
                <ListGroup>
                    <ListGroupItem className="border-0 px-0 total">
                        <h5>Total</h5>
                        <span>S/.{totalAmount}</span>
                    </ListGroupItem>
                </ListGroup>
                
                {/* Componente de pago de MercadoPago */}
                <Payment
                    tour={tour}
                    quantity={quantity}
                    totalPrice={totalAmount}
                    booking={booking}
                    user={user}
                    handleOpenModal={handleOpenModal}
                />
            </div>
            {/*============== booking botton  end ====================*/}

            {/*============== Modal ====================*/}
            <LoginModal
                isModalOpen={isModalOpen}
                toggleModal={toggleModal}
                handleLogin={handleLogin}
                credentials={credentials}
                setCredentials={setCredentials}
            />
            {/*============== Modal end ====================*/}
        </div>
    );
};

export default Booking;
