import React, { useState, useEffect, useContext } from "react";
import "./booking.css";
import Payment from "../Payment/Payment.jsx";
import {
    Form,
    FormGroup,
    ListGroup,
    ListGroupItem,
} from "reactstrap";
import LoginModal from "../Login-Modal/LoginModal.jsx";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import { BASE_URL } from "../../utils/config";

import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Booking = ({ tour, avgRating }) => {
    // ...

    // Nuevos estados para almacenar los DNI y los datos de las personas
    const [guestDnis, setGuestDnis] = useState([]);
    const [guestData, setGuestData] = useState([]);
    const [areDnisValid, setAreDnisValid] = useState(false);

    // Se llama cuando cambia el número de invitados
    useEffect(() => {
        const newDnis = new Array(booking.guestSize).fill("");
        setGuestDnis(newDnis);
        const newData = new Array(booking.guestSize).fill(null);
        setGuestData(newData);
    }, [booking.guestSize]);

    // ...

    const handleDniChange = (index, dni) => {
        const newDnis = [...guestDnis];
        newDnis[index] = dni;
        setGuestDnis(newDnis);
    };

    const fetchDniData = async (index, dni) => {
        // Validar el DNI aquí
        try {
            const res = await fetch(
                'https://dniruc.apisperu.com/api/v1/dni/' +
                dni +
                '?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJlbWFpbCI6ImtldmluZmdlMjQxMEBnbWFpbC5jb20ifQ.Ha6IN3AcX5zAC1pBssILUz1U8lg03rodOkh2rpxjKag'
            );

            const data = await res.json();

            // Actualizar los datos de los invitados
            const newData = [...guestData];
            newData[index] = data;
            setGuestData(newData);

            // Comprobar si todos los DNI son válidos
            const areAllValid = newData.every((d) => d && d.success);
            setAreDnisValid(areAllValid);
        } catch (error) {
            console.error('Error al obtener datos:', error);
            alert('No se pudo obtener los datos. Por favor, verifique el DNI e intente nuevamente.');
        }
    };


    return (
        <div className="booking">
            {/* ... */}

            {/*============== booking form ====================*/}
            <div className="booking__form">
                {/* ... */}
                <FormGroup>
                    {guestDnis.map((dni, index) => (
                        <div key={index}>
                            <input
                                type="number"
                                placeholder="DNI"
                                required
                                value={dni}
                                onChange={(e) => handleDniChange(index, e.target.value)}
                            />
                            <button
                                type="button"
                                onClick={() => fetchDniData(index, dni)}
                            >Validar
                            </button>
                            {guestData[index] && guestData[index].success && (
                                <p>
                                    {guestData[index].nombres} {guestData[index].apellidoPaterno} {guestData[index].apellidoMaterno}
                                </p>
                            )}
                        </div>
                    ))}
                </FormGroup>
            </div>
            {/*============== booking end ====================*/}

            {/*============== booking botton ====================*/}
            <div className="booking__botton">
                {/* ... */}

                {/* Componente de pago de MercadoPago */}
                <Payment
                    tour={tour}
                    quantity={quantity}
                    totalPrice={totalAmount}
                    booking={booking}
                    user={user}
                    handleOpenModal={handleOpenModal}
                    disabled={!areDnisValid} // Se deshabilita si los DNI no son válidos
                />
            </div>
            {/*============== booking botton  end ====================*/}

            {/*============== Modal ====================*/}
            <LoginModal
                isModalOpen={isModalOpen}
                toggleModal={toggleModal}
                handleLogin={handleLogin}
                credentials={credentials}
                setCredentials={setCredentials}
            />
            {/*============== Modal end ====================*/}
        </div>
    );
};

export default Booking;
