import React, { useState } from "react";
import { Container, Row, Col, Form, FormGroup, Button } from 'reactstrap';
import { Link, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { BASE_URL } from './../utils/config';
import 'react-toastify/dist/ReactToastify.css';
import '../styles/forgotpassword.css'; // Importa tu archivo CSS personalizado
import userIcon from '../assets/images/user.png';
import loginImg from '../assets/images/login.png';

const ForgotPassword = () => {
    const [email, setEmail] = useState("");
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await fetch(`${BASE_URL}/auth/forgot-password`, {
                method: 'post',
                headers: {
                    'content-type': 'application/json',
                },
                body: JSON.stringify({ email }),
            });
            const result = await res.json();

            if (!res.ok) {
                toast.error(result.message);
            } else {
                toast.success("Email sent. Please check your email for further instructions.");
                navigate("/");
            }
        } catch (err) {
            toast.error(err.message);
        }
    }

    return (
        <Container>
            <Row>
                <Col lg='8' className="m-auto">
                    <div className="forgot-password__container">
                    <div className="login__img">
                                <img src={loginImg} alt="" />
                            </div>

                            <div className="login__form">
                                <div className="user">
                                    <img src={userIcon} alt="" />
                                </div>
                        <h2>Forgot Password</h2>
                        <Form onSubmit={handleSubmit}>
                            <FormGroup>
                                <input type="email" placeholder="Email" required id="email" value={email} onChange={e => setEmail(e.target.value)} />
                            </FormGroup>
                            <Button className="btn secondary__btn auth__btn" type="submit">
                                Send Reset Email
                            </Button>
                        </Form>
                        <p>Remember your password? <Link to='/login'>Login</Link></p>
                    </div>
                    </div>
                </Col>
            </Row>
        </Container>
    );
};

export default ForgotPassword;
