import React, { useEffect, useRef, useState, useContext } from "react";
import "../styles/tour-details.css";
import { Container, Row, Col, Form, ListGroup } from "reactstrap";
import { useParams } from "react-router-dom";
import calculateAvgRating from "./../utils/avgRating";
import avatar from "../assets/images/avatar.jpg";
import Booking from "../components/Booking/Booking";
import Newsletter from "./../shared/Newsletter";
import useFetch from "./../hooks/useFetch";
import { BASE_URL } from "./../utils/config";
import { AuthContext } from "./../context/AuthContext";
import LoginModal from "../components/Login-Modal/LoginModal.jsx";
import { Oval } from "react-loader-spinner";
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import ReactPaginate from 'react-paginate';

const TourDetails = () => {
  const { id } = useParams();
  const reviewMsgRef = useRef("");
  const { user } = useContext(AuthContext);
  const [currentPage, setCurrentPage] = useState(0);
  const pageSize = 8; // Número de elementos por página

  // fetch data from database
  const { data: tour, loading, error } = useFetch(`${BASE_URL}/tours/${id}`);

  // destructure properties from tour object
  const {
    photo,
    title,
    desc,
    price,
    address,
    reviews,
    city,
    distance,
    maxGroupSize,
  } = tour || {};

  const { totalRating, avgRating } = calculateAvgRating(reviews);

  const [selectedRating, setSelectedRating] = useState(null);

  const handlePageChange = (selectedPage) => {
    setCurrentPage(selectedPage.selected);
  };

  // format date
  const options = { day: "numeric", month: "long", year: "numeric" };

  // Modal
  const [isModalOpen, setIsModalOpen] = useState(false);
  const toggleModal = () => setIsModalOpen(!isModalOpen);
  const handleOpenModal = (e) => {
    if (!user) {
      setIsModalOpen(true);
    }
  };

  // submit request to the server
  const submitHandler = async (e) => {
    e.preventDefault();
    const reviewText = reviewMsgRef.current.value;

    try {
      if (!user || user === undefined || user === null) {
        return handleOpenModal();
      }

      const reviewObj = {
        username: user?.username,
        reviewText,
        rating: selectedRating,
      };

      const res = await fetch(`${BASE_URL}/review/${id}`, {
        method: "post",
        headers: {
          "content-type": "application/json",
        },
        credentials: "include",
        body: JSON.stringify(reviewObj),
      });

      const result = await res.json();
      if (!res.ok) {
        return toast.error(result.message);
      }

      window.location.reload();
    } catch (err) {
      toast.error(err.message);
    }
  };

  useEffect(() => {
    window.scrollTo(0, 0);
  }, [tour]);

  return (
    <>
      <section>
        <Container>
          {loading && (
            <h4 className="text-center pt-5">
              <Oval
                color="#f11c00"
                height={100}
                width={100}
                timeout={3000} //3 secs
              />
            </h4>
          )}
          {error && <h4 className="text-center pt-5">{error}</h4>}
          {!loading && !error && (
            <Row>
              <Col lg="8">
                <div className="tour__content">
                  <img src={photo} alt="" />

                  <div className="tour__info">
                    <h2>{title}</h2>

                    <div className="d-flex align-items-center gap-5">
                      <span className="tour__rating d-flex align-items-center gap-1">
                        <i
                          className="ri-star-s-fill"
                          style={{ color: "var(--secondary-color)" }}
                        ></i>
                        {avgRating === 0 ? null : avgRating}
                        {totalRating === 0 ? (
                          "Not rated"
                        ) : (
                          <span>({reviews?.length})</span>
                        )}
                      </span>
                      <span>
                        <i className="ri-map-pin-user-fill"></i> {address}
                      </span>
                    </div>
                    <div className="tour__extra-details">
                      <span>
                        <i className="ri-map-pin-2-line"></i> {city}
                      </span>
                      <span>
                        <i className="ri-money-dollar-circle-line"></i> S/.{price}{" "}
                        /por person
                      </span>
                      <span>
                        <i className="ri-map-pin-time-line"></i> {distance} k/m
                      </span>
                      <span>
                        <i className="ri-group-line"></i> {maxGroupSize} personas
                      </span>
                    </div>
                    <h5>Descripcion</h5>
                    <p>{desc}</p>
                  </div>

                  {/* ========== tour reviews section =========== */}
                  <div className="tour__reviews mt-4">
                    <h4>Reviews ({reviews?.length} reviews)</h4>

                    <Form onSubmit={submitHandler}>
                      <div className="d-flex align-items-center gap-3 mb-4 rating__group">
                        {[1, 2, 3, 4, 5].map((rating) => (
                          <span
                            key={rating}
                            onClick={() => setSelectedRating(rating)}
                          >
                            {rating}
                            <i
                              className="ri-star-s-fill"
                              style={{
                                color:
                                  selectedRating >= rating
                                    ? "var(--secondary-color)"
                                    : "black",
                              }}
                            ></i>
                          </span>
                        ))}
                      </div>

                      <div className="review__input">
                        <input
                          type="text"
                          ref={reviewMsgRef}
                          placeholder="Danos tu opinion"
                          required
                        />
                        <button
                          className="btn primary__btn text-white"
                          type="submit"
                        >
                          Enviar
                        </button>
                      </div>
                    </Form>

                    <ListGroup className="user__reviews">
                    {reviews && reviews
  .slice(currentPage * pageSize, (currentPage + 1) * pageSize)
  .map((review, index) => (
    <div className="review__item" key={index}>
      <img src={avatar} alt="" />

      <div className="w-100">
        <div className="d-flex align-items-center justify-content-between">
          <div>
            <h5>{review.username}</h5>
            <p>
              {new Date(review.createdAt).toLocaleDateString(
                "es-PE",
                options
              )}
            </p>
          </div>
          <span className="d-flex align-items-center">
            {review.rating}
            <i className="ri-star-s-fill"></i>
          </span>
        </div>

        <h6>{review.reviewText}</h6>
      </div>
    </div>
  ))
}
                    </ListGroup>
                    <ReactPaginate
                      previousLabel={'<'}
                      nextLabel={'>'}
                      breakLabel={'...'}
                      breakClassName={'pagination-break'}
                      pageCount={Math.ceil(reviews.length / pageSize)}
                      marginPagesDisplayed={2}
                      pageRangeDisplayed={5}
                      onPageChange={handlePageChange}
                      containerClassName={'pagination'}
                      activeClassName={'active'}
                    />

                  </div>
                  {/* ========== tour reviews section end =========== */}

                </div>
              </Col>
              <Col lg="4">
                <Booking tour={tour} avgRating={avgRating} />
              </Col>
            </Row>
          )}
        </Container>
      </section>
      {/*============== Modal ====================*/}
      <LoginModal
        isModalOpen={isModalOpen}
        toggleModal={toggleModal}
      />
      {/*============== Modal end ====================*/}
      <Newsletter />
    </>
  );
};

export default TourDetails;
