import React from 'react';
import { toast } from 'react-toastify';
import axios from 'axios';
import { FormGroup, } from "reactstrap";

const DniField = ({ index, dni, setDni, userData, setUserData }) => {
    const fetchDniData = async () => {
        if (dni.length === 8) {
            try {
                const response = await axios.get(`https://dniruc.apisperu.com/api/v1/dni/${dni}?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJlbWFpbCI6ImtldmluZmdlMjQxMEBnbWFpbC5jb20ifQ.Ha6IN3AcX5zAC1pBssILUz1U8lg03rodOkh2rpxjKag`);
    
                console.log(response.data); // Añade esta línea
    
                if (response.data && response.data.nombres && response.data.apellidoPaterno && response.data.apellidoMaterno) {
                    setUserData(response.data);
                } else {
                    toast.error("La respuesta del servidor no contiene los datos esperados.");
                }
            } catch (error) {
                toast.error("Error al obtener los datos del DNI.");
                console.error("Error al obtener los datos del DNI: ", error);
            }
        } else {
            toast.error("Por favor, introduce un DNI válido.");
        }

};

return (
    <FormGroup>
        <input
            type="number"
            placeholder="DNI"
            value={dni || ''}
            onChange={(e) => {
                let tempDni = [...dni];
                tempDni[index] = e.target.value;
                setDni(tempDni);
            }}
        />
        <button onClick={fetchDniData}>
            Validar
        </button>
        {userData && userData.nombres && <p>Nombres: {userData.nombres}</p>}
        {userData && userData.apellidoPaterno && userData.apellidoMaterno &&
            <p>Apellidos: {userData.apellidoPaterno} {userData.apellidoMaterno}</p>}
    </FormGroup>
)
};

export default DniField;
