import React, { useState, useEffect, useContext } from "react";
import "./booking.css";
import Payment from "../Payment/Payment.jsx";
import {
    Form,
    FormGroup,
    ListGroup,
    ListGroupItem,
} from "reactstrap";
import LoginModal from "../Login-Modal/LoginModal.jsx";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import { BASE_URL } from "../../utils/config";

import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Booking = ({ tour, avgRating }) => {
    const { price, reviews, title } = tour;
    const navigate = useNavigate();
    const { user } = useContext(AuthContext);


     // Nuevos estados para almacenar los DNI y los datos de las personas
     const [guestDnis, setGuestDnis] = useState([]);
     const [guestData, setGuestData] = useState([]);
     const [areDnisValid, setAreDnisValid] = useState(false);
 
     // Se llama cuando cambia el número de invitados
     useEffect(() => {
         const newDnis = new Array(booking.guestSize).fill("");
         setGuestDnis(newDnis);
         const newData = new Array(booking.guestSize).fill(null);
         setGuestData(newData);
     }, [booking.guestSize]);

     const handleDniChange = (index, dni) => {
        const newDnis = [...guestDnis];
        newDnis[index] = dni;
        setGuestDnis(newDnis);
    };


    const [booking, setBooking] = useState({
        userId: user && user._id,
        userEmail: user && user.email,
        tourName: title,
        phone: "",
        guestSize: 1,
        booAt: "",
    });

    const serviceFee = 10;
    const totalAmount =
        Number(price) * Number(booking.guestSize) + Number(serviceFee);

    const { dispatch } = useContext(AuthContext);

    const [credentials, setCredentials] = useState({
        email: undefined,
        password: undefined,
    });

    const quantity = booking.guestSize;

    const handleOpenModal = () => {
        setIsModalOpen(true);
    };

    const currentURL = window.location.pathname;
    const [isModalOpen, setIsModalOpen] = useState(false);
    const toggleModal = () => setIsModalOpen(!isModalOpen);

    const handleChange = (e) => {
        setBooking((prev) => ({ ...prev, [e.target.id]: e.target.value }));
    };


    const fetchDniData = async (index, dni) => {
        // Validar el DNI aquí
        try {
            const res = await fetch(
                'https://dniruc.apisperu.com/api/v1/dni/' +
                dni +
                '?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJlbWFpbCI6ImtldmluZmdlMjQxMEBnbWFpbC5jb20ifQ.Ha6IN3AcX5zAC1pBssILUz1U8lg03rodOkh2rpxjKag'
            );

            const data = await res.json();

            // Actualizar los datos de los invitados
            const newData = [...guestData];
            newData[index] = data;
            setGuestData(newData);

            // Comprobar si todos los DNI son válidos
            const areAllValid = newData.every((d) => d && d.success);
            setAreDnisValid(areAllValid);
        } catch (error) {
            console.error('Error al obtener datos:', error);
            alert('No se pudo obtener los datos. Por favor, verifique el DNI e intente nuevamente.');
        }
    };


    const handleLogin = async (e) => {
        e.preventDefault();

        try {
            const res = await fetch(`${BASE_URL}/auth/login`, {
                method: "post",
                headers: {
                    "content-type": "application/json",
                },
                credentials: "include",
                body: JSON.stringify(credentials),
            });

            const result = await res.json();

            if (!res.ok) toast.error(result.message);

            console.log(result.data);

            dispatch({ type: "LOGIN_SUCCESS", payload: result.data });
            setIsModalOpen(false); // Cierra el modal antes de redirigir al usuario
            navigate(currentURL); // Redirige al usuario a la página del tour actual en lugar de la página de inicio
        } catch (err) {
            dispatch({ type: "LOGIN_FAILURE", payload: err.message });
        }
    };


    return (
        <div className="booking">
            <div className="booking__top d-flex align-items-center justify-content-between">
                <h3>
                    S/.{price} <span>/por persona</span>
                </h3>

                <span className="tour__rating d-flex align-items-center">
                    <i className="ri-star-s-fill"></i>
                    {avgRating === 0 ? null : avgRating} ({reviews?.length})
                </span>
            </div>

            {/*============== booking form ====================*/}
            <div className="booking__form">
                <h5>Informacion</h5>
                <Form className="booking__info-form" onSubmit={(e) => e.preventDefault()}>
                    <FormGroup>
                    {guestDnis.map((dni, index) => (
                        <div key={index}>
                            <input
                                type="number"
                                placeholder="DNI"
                                required
                                value={dni}
                                onChange={(e) => handleDniChange(index, e.target.value)}
                            />
                            <button
                                type="button"
                                onClick={() => fetchDniData(index, dni)}
                            >
                               




                                Validar
                            </button>
                            {guestData[index] && guestData[index].success && (
                                <p>
                                    {guestData[index].nombres} {guestData[index].apellidoPaterno} {guestData[index].apellidoMaterno}
                                </p>
                            )}
                        </div>
                    ))}
                </FormGroup>

                </Form>
            </div>
            {/*============== booking end ====================*/}

            {/*============== booking botton ====================*/}
            <div className="booking__botton">
                <ListGroup>
                    <ListGroupItem className="border-0 px-0">
                        <h5 className="d-flex align-items-center gap-1">
                            S/.{price} <i className="ri-close-line"></i> 1 persona
                        </h5>
                        <span>S/.{price}</span>
                    </ListGroupItem>
                </ListGroup>
                <ListGroup>
                    <ListGroupItem className="border-0 px-0">
                        <h5>Cargo por servicio</h5>
                        <span>S/.{serviceFee}</span>
                    </ListGroupItem>
                </ListGroup>
                <ListGroup>
                    <ListGroupItem className="border-0 px-0 total">
                        <h5>Total</h5>
                        <span>S/.{totalAmount}</span>
                    </ListGroupItem>
                </ListGroup>

                {/* Componente de pago de MercadoPago */}
                <Payment
                    tour={tour}
                    quantity={quantity}
                    totalPrice={totalAmount}
                    booking={booking}
                    user={user}
                    handleOpenModal={handleOpenModal}
                />
            </div>
            {/*============== booking botton  end ====================*/}

            {/*============== Modal ====================*/}
            <LoginModal
                isModalOpen={isModalOpen}
                toggleModal={toggleModal}
                handleLogin={handleLogin}
                credentials={credentials}
                setCredentials={setCredentials}
            />
            {/*============== Modal end ====================*/}
        </div>
    );
};

export default Booking;
