import React from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { BASE_URL } from '../../utils/config';
import '../../styles/contact/deleteContact.css';
import { useNavigate } from 'react-router-dom';

const DeleteContact = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const handleBack = () => {
    navigate('/manage_contacts');
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`${BASE_URL}/contact/${id}`);
      alert('Contacto eliminado exitosamente!');
    } catch (error) {
      alert('Ocurrió un error al eliminar el contacto');
    }
  };

  return (
    <div className="DeleteContact">
      <h2>¿Estás seguro de que quieres eliminar este contacto?</h2>
      <button onClick={handleDelete}>Eliminar contacto</button>
      <button onClick={handleBack}>Regresar</button>
    </div>
  );
};

export default DeleteContact;
