import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { BASE_URL } from '../../utils/config';
import './manageSubscribes.css';
import { toast } from 'react-toastify';

const ManageSubscribes = () => {
    const [subscribes, setSubscribes] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchSubscribes = async () => {
            try {
                const res = await axios.get(`${BASE_URL}/subscribe`);
                setSubscribes(res.data.data);
                setLoading(false);
            } catch (error) {
                toast.error("An error occurred while fetching subscribes");
                setLoading(false);
            }
        };

        fetchSubscribes();
    }, []);

    if (loading) {
        return <p>Loading...</p>;
    }

    if (!Array.isArray(subscribes)) {
        return <p>No subscribes available</p>;
    }

    return (
        <div>
            <h2>Manage Subscribes</h2>
            {subscribes.length === 0 ? (
                <p>No subscribes available</p>
            ) : (
                <table>
                    <thead>
                        <tr>
                            <th>Email</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {subscribes.map((subscribe) => (
                            <tr key={subscribe._id}>
                                <td>{subscribe.email}</td>
                                <td>
                                    <Link to={`/edit_subscribe/${subscribe._id}`} className="btn secondary__btn">
                                        Edit
                                    </Link>
                                    <Link to={`/delete_subscribe/${subscribe._id}`} className="btn secondary__btn">
                                        Delete
                                    </Link>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
            <button className="btn secondary__btn">
                <Link to="/create_subscribe">Create new subscribe</Link>
            </button>
        </div>
    );
}
export default ManageSubscribes;
