import React, { useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { BASE_URL } from '../../utils/config';
import { useNavigate } from 'react-router-dom';

const DeleteReview = () => {
  const { reviewId } = useParams();
  const navigate = useNavigate(); // Hook useNavigate
  const [loading, setLoading] = useState(true);

  const handleBack = () => {
    navigate('/manage_reviews');
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`${BASE_URL}/review/${reviewId}`);
      alert('Revisión eliminada exitosamente!');
      navigate('/manage_reviews'); // After successful delete, navigate back to the review list
    } catch (error) {
      alert('Ocurrió un error al eliminar la revisión');
    }
  };


  if (loading) {
    return <p>Eliminando revisión...</p>;
  }

  return (
    <div className="DeleteReview">
      <h2>¿Estás seguro de que quieres eliminar esta revisión?</h2>
      <button onClick={handleDelete}>Eliminar revisión</button>
      <button onClick={handleBack}>Regresar</button>
    </div>
  );
};

export default DeleteReview;
