import React, { useState, useContext } from "react";
import "./booking.css";
import Payment from "../Payment/Payment.jsx";
import {
    Form,
    FormGroup,
    ListGroup,
    ListGroupItem,
    Button,
} from "reactstrap";

import LoginModal from "../Login-Modal/LoginModal.jsx";

import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import { BASE_URL } from "../../utils/config";

import fetchDniData from "../../utils/fetchDniData";

const Booking = ({ tour, avgRating }) => {
    const { price, reviews, title } = tour;
    const navigate = useNavigate();

    const { user } = useContext(AuthContext);

    const [booking, setBooking] = useState({
        userId: user && user._id,
        userEmail: user && user.email,
        tourName: title,
        fulName: "",
        phone: "",
        guestSize: 1,
        booAt: "",
        dnis: {},
    });

    const serviceFee = 10;
    const totalAmount =
        Number(price) * Number(booking.guestSize) + Number(serviceFee);

    const { dispatch } = useContext(AuthContext);

    const [credentials, setCredentials] = useState({
        email: undefined,
        password: undefined,
    });

    const quantity = booking.guestSize;

    const currentURL = window.location.pathname;
    const [isModalOpen, setIsModalOpen] = useState(false);
    const toggleModal = () => setIsModalOpen(!isModalOpen);

    const handleOpenModal = (e) => {
        if (!user) {
            setIsModalOpen(true);
        }
    };

    const handleChange = (e) => {
        if (e.target.id.startsWith("dni")) {
            const index = e.target.id.slice(3);
            setBooking((prev) => ({
                ...prev,
                dnis: { ...prev.dnis, [index]: e.target.value },
            }));
        } else {
            setBooking((prev) => ({ ...prev, [e.target.id]: e.target.value }));
        }
    };

    const renderDniInputs = () => {
        const dniInputs = [];
    
        for (let i = 1; i <= booking.guestSize; i++) {
            dniInputs.push(
                <FormGroup key={i}>
                    <input
                        type="text"
                        placeholder={`DNI persona ${i}`}
                        id={`dni${i}`}
                        required
                        onChange={handleChange}
                    />
                </FormGroup>
            );
        }
    
        return dniInputs;
    };
    


    const [dniList, setDniList] = useState([]);

    

    //send data to the server

    const handleClik = async (e) => {
        e.preventDefault();
    
        console.log(booking);
    
        try {
            if (!user || user === undefined || user === null) {
                return handleOpenModal();
            }
    
            // Check if all required fields are filled in
            if (!booking.phone || !booking.bookAt || !booking.guestSize) {
                return alert("Please fill in all required fields.");
            }
    
            // Fetch DNI data for each guest
            const dniDataList = [];
            for (const dni of dniList) {
                const dniData = await fetchDniData(dni);
                if (!dniData) {
                    return alert(`Error al buscar DNI ${dni}. Por favor, verifique el DNI e intente nuevamente.`);
                }
                dniDataList.push(dniData);
            }
    
            // Add DNI data to booking object
            const updatedBooking = {
                ...booking,
                dniDataList,
            };
    
            const res = await fetch(`${BASE_URL}/booking`, {
                method: "post",
                headers: {
                    "content-type": "application/json",
                },
                credentials: "include",
                body: JSON.stringify(updatedBooking),
            });
    
            const result = await res.json();
    
            if (!res.ok) {
                return alert(result.message);
            }
            navigate("/thank-you");
        } catch (err) {
            alert(err.message);
        }
    };
    

    const isPagarDisabled =
        !booking.phone ||
        !booking.bookAt ||
        !booking.guestSize ||
        booking.phone.trim() === "" ||
        booking.bookAt.trim() === "";

    const handleLogin = async (e) => {
        e.preventDefault();

        try {
            const res = await fetch(`${BASE_URL}/auth/login`, {
                method: "post",
                headers: {
                    "content-type": "application/json",
                },
                credentials: "include",
                body: JSON.stringify(credentials),
            });

            const result = await res.json();

            if (!res.ok) alert(result.message);

            console.log(result.data);

            dispatch({ type: "LOGIN_SUCCESS", payload: result.data });
            setIsModalOpen(false); // Cierra el modal antes de redirigir al usuario
            navigate(currentURL); // Redirige al usuario a la página del tour actual en lugar de la página de inicio
        } catch (err) {
            dispatch({ type: "LOGIN_FAILURE", payload: err.message });
        }
    };

    return (
        <div className="booking">
            <div className="booking__top d-flex align-items-center justify-content-between">
                <h3>
                    S/.{price} <span>/por persona</span>
                </h3>

                <span className="tour__rating d-flex align-items-center">
                    <i className="ri-star-s-fill"></i>
                    {avgRating === 0 ? null : avgRating} ({reviews?.length})
                </span>
            </div>

            {/*============== booking form ====================*/}
            <div className="booking__form">
                <h5>Informacion</h5>
                <Form className="booking__info-form" onSubmit={handleClik}>
                    <FormGroup>
                        <input
                            type="number"
                            placeholder="Telefono"
                            id="phone"
                            required
                            onChange={handleChange}
                        />
                    </FormGroup>
                    <FormGroup className="d-flex align-items-center gap-3">
                        <input
                            type="date"
                            placeholder=""
                            id="bookAt"
                            required
                            onChange={handleChange}
                        />
                        <input
                            type="number"
                            placeholder="Guest"
                            id="guestSize"
                            required
                            onChange={handleChange}
                        />
                    </FormGroup>
                    {renderDniInputs()}
                </Form>
            </div>
            {/*============== booking end ====================*/}

            {/*============== booking botton ====================*/}
            <div className="booking__botton">
                <ListGroup>
                    <ListGroupItem className="border-0 px-0">
                        <h5 className="d-flex align-items-center gap-1">
                            S/.{price} <i className="ri-close-line"></i> 1 persona
                        </h5>
                        <span>S/.{price}</span>
                    </ListGroupItem>
                </ListGroup>
                <ListGroup>
                    <ListGroupItem className="border-0 px-0">
                        <h5>Cargo por servicio</h5>
                        <span>S/.{serviceFee}</span>
                    </ListGroupItem>
                </ListGroup>
                <ListGroup>
                    <ListGroupItem className="border-0 px-0 total">
                        <h5>Total</h5>
                        <span>S/.{totalAmount}</span>
                    </ListGroupItem>
                </ListGroup>

                <Button
                    className="btn primary__btn w-100 mt-4"
                    onClick={handleClik}
                    disabled={isPagarDisabled}
                >
                    Confirmar
                </Button>

                {/* Componente de pago de MercadoPago */}
                <Payment tour={tour} quantity={quantity} totalPrice={totalAmount} />

            </div>
            {/*============== booking botton  end ====================*/}

            {/*============== Modal ====================*/}
            <LoginModal
                isModalOpen={isModalOpen}
                toggleModal={toggleModal}
                handleLogin={handleLogin}
                credentials={credentials}
                setCredentials={setCredentials}
            />
            {/*============== Modal end ====================*/}
        </div>
    );
};

export default Booking;
