import React, { useState } from "react";

function DniField({ index, dni, setDni }) {
  const [userData, setUserData] = useState(null);

  const fetchDniData = async (dni) => {
    try {
      const response = await fetch(
        `https://dniruc.apisperu.com/api/v1/dni/${dni}?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJlbWFpbCI6ImtldmluZmdlMjQxMEBnbWFpbC5jb20ifQ.Ha6IN3AcX5zAC1pBssILUz1U8lg03rodOkh2rpxjKag

        `
      );
      const data = await response.json();

      setUserData(data);
    } catch (error) {
      console.error("Error al obtener los datos del DNI: ", error);
      alert("Error al obtener los datos del DNI");
    }
  };

  return (
    <div>
      <input
        type="number"
        placeholder="DNI"
        value={dni || ""}
        onChange={(e) => {
          let tempDni = [...dni];
          tempDni[index] = e.target.value;
          setDni(tempDni);
        }}
      />
      <button onClick={() => fetchDniData(dni[index])}>Validar</button>

      {userData && userData.nombres && <p>Nombres: {userData.nombres}</p>}
      {userData &&
        userData.apellidoPaterno &&
        userData.apellidoMaterno && (
          <p>
            Apellidos: {userData.apellidoPaterno} {userData.apellidoMaterno}
          </p>
        )}
    </div>
  );
}

export default DniField;
