// DniField.jsx
import React from 'react';
import { FormGroup, } from "reactstrap";

const DniField = ({ index, dni, setDni, userData, setUserData }) => {
    const fetchDniData = async (dni, index) => {
        try {
            const response = await fetch(`http://localhost:3000/api/getDniData/${dni}`);
    
            // Comprobar el estado de la respuesta
            if (!response.ok) {
                console.error(`Error al obtener los datos del DNI: ${response.statusText}`);
                alert("Error al obtener los datos del DNI");
                return;
            }
    
            // Intentar convertir la respuesta a JSON
            let data;
            try {
                data = await response.json();
            } catch (error) {
                console.error("Error al convertir la respuesta a JSON: ", error);
                alert("Error al convertir la respuesta a JSON");
                return;
            }
    
            // Aquí 'data' es la respuesta de tu API de Reniec
    
            // Ahora, actualiza el estado con los nuevos datos
            setUserData(prev => {
                const newUserData = [...prev];
                newUserData[index] = data;
                return newUserData;
            });
    
        } catch (error) {
            console.error("Error al obtener los datos del DNI: ", error);
            alert("Error al obtener los datos del DNI");
        }
    };
    


    return (
        <FormGroup>
            <input
                type="number"
                placeholder="DNI"
                value={dni || ''}
                onChange={(e) => {
                    let tempDni = [...dni];
                    tempDni[index] = e.target.value;
                    setDni(tempDni);
                }}
            />
            <button onClick={() => fetchDniData(dni[index], index)}>
                Validar
            </button>

            {userData && userData.nombres && <p>Nombres: {userData.nombres}</p>}
            {userData && userData.apellidoPaterno && userData.apellidoMaterno &&
                <p>Apellidos: {userData.apellidoPaterno} {userData.apellidoMaterno}</p>}
        </FormGroup>
    );
};

export default DniField;
