import React, { useEffect, useRef, useState } from 'react';

const PaymentButton = () => {
    const [sdkReady, setSdkReady] = useState(false);
    const checkoutRef = useRef(null);

    useEffect(() => {
        const loadScript = async () => {
            const script = document.createElement('script');
            script.src = 'https://sdk.mercadopago.com/js/v2';
            script.onload = () => {
                setSdkReady(true);
            };
            document.body.appendChild(script);

            const scriptSecurity = document.createElement('script');
            scriptSecurity.src = 'https://www.mercadopago.com/v2/security.js';
            scriptSecurity.setAttribute('view', 'item');
            document.body.appendChild(scriptSecurity);
        };
        loadScript();
    }, []);

    useEffect(() => {
        if (!sdkReady) {
            return;
        }

        const publicKey = 'APP_USR-41c1c933-d1d8-410e-ae0f-15854d54b2d1';
        checkoutRef.current = new window.mercadopago.Checkout({
            publicKey,
        });
    }, [sdkReady]);

    const handlePayment = async () => {
        console.log('handlePayment called');
        const response = await fetch('/api/v1/payment/create_preference', { method: 'POST' });
        const data = await response.json();
        const preferenceId = data.preferenceId;

        checkoutRef.current.update({ preferenceId });
        console.log("Attempting to open checkout", checkoutRef.current);
        checkoutRef.current.open();
    };

    return (
        <div>
            <button onClick={handlePayment} disabled={!sdkReady}>Bot√≥n de prueba</button>
        </div>
    );
};

export default PaymentButton;
