import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import { BASE_URL } from '../../utils/config';
import 'react-toastify/dist/ReactToastify.css';

const ManageReviews = () => {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const res = await axios.get(`${BASE_URL}/review`);
        console.log(res.data);
        setReviews(res.data.data);
        setLoading(false);
      } catch (error) {
        console.error(error);
        toast.error("Error al obtener las revisiones");
        setLoading(false);
      }
    };

    fetchReviews();
  }, []);

  const getTourTitle = async (tourId) => {
    try {
      const res = await axios.get(`${BASE_URL}/tours/${tourId}`);
      return res.data.data.title;
    } catch (error) {
      console.error(error);
      return 'N/A';
    }
  };

  if (loading) {
    return <p>Cargando...</p>;
  }

  if (!Array.isArray(reviews) || reviews.length === 0) {
    return <p>No hay revisiones disponibles</p>;
  }

  const renderReviews = async () => {
    const reviewItems = await Promise.all(
      reviews.map(async (review) => {
        const tourTitle = await getTourTitle(review.tourId);
        return (
          <tr key={review._id}>
            <td>{tourTitle}</td>
            <td>{review.username}</td>
            <td>{review.reviewText}</td>
            <td>{review.rating}</td>
            <td>
              <Link to={`/delete_review/${review._id}`} className="btn secondary__btn">
                Eliminar
              </Link>
            </td>
          </tr>
        );
      })
    );
    return reviewItems;
  };

  return (
    <div>
      <h2>Gestionar Revisiones</h2>
      <table>
        <thead>
          <tr>
            <th>Tour</th>
            <th>Usuario</th>
            <th>Comentario</th>
            <th>Calificaci√≥n</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>{renderReviews()}</tbody>
      </table>
    </div>
  );
};

export default ManageReviews;
