import React, { useState, useEffect } from "react";
import { Button } from "reactstrap";
import { BASE_URL } from "../../utils/config";

import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Payment = ({ tour, quantity, totalPrice, booking, user, handleOpenModal, dni, userData }) => {
    const [initPoint, setInitPoint] = useState(null);

    useEffect(() => {
        const createPaymentPreference = async () => {
            try {
                const response = await fetch(`${BASE_URL}/mercadopago/create_payment`, {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify({
                        product: {
                            id: tour.id,
                            title: tour.title,
                            unit_price: tour.price
                        },
                        quantity: parseInt(quantity, 10),
                        totalPrice,
                        payer: {
                            // Aquí debes pasar la información del comprador
                            // En base a la información que tienes disponible
                            name: user.name,
                            surname: user.surname,
                            email: user.email,
                            date_created: new Date().toISOString(),
                            phone: {
                                area_code: "11",
                                number: "4444-4444"
                            },
                            identification: {
                                type: "DNI",
                                number: dni
                            },
                            address: {
                                street_name: "Street",
                                street_number: 123,
                                zip_code: "12345678"
                            }
                        }
                    }),
                });
                if (!response.ok) {
                    throw new Error("Error al crear la preferencia de pago de MercadoPago");
                }
                const data = await response.json();
                setInitPoint(data.init_point);
            } catch (error) {
                console.error(error.message);
            }
        };

        createPaymentPreference();
    }, [tour, quantity, totalPrice, user, dni]);

    const handlePayment = async () => {
        console.log("Init point:", initPoint);
        try {
            if (!user || user === undefined || user === null) {
                return handleOpenModal();
            }
            if (dni.some((val) => val === "") || userData.some((data) => Object.keys(data).length === 0)) {
                toast.error("Por favor, completa todos los campos de DNI.");
                return;
            }
            if (!booking.phone || !booking.bookAt || !booking.guestSize) {
                return toast.error("Please fill in all required fields.");
            }

            if (initPoint) {
                window.open(initPoint, "_blank");
            }

        } catch (err) {
            toast.error(err.message);
        }
    };

    return (
        <div className="payment">
            <Button
                className="btn primary__btn w-100 mt-4"
                onClick={handlePayment}
                disabled={!initPoint}
            >
                Pagar
            </Button>
        </div>
    );
};

export default Payment;
