import React, { useEffect, useRef, useState, useContext } from "react";
import "../styles/tour-details.css";
import { Container, Row, Col, Form, ListGroup } from "reactstrap";
import { useParams } from "react-router-dom";
import calculateAvgRating from "./../utils/avgRating";
import avatar from "../assets/images/avatar.jpg";
import Booking from "../components/Booking/Booking";
import Newsletter from "./../shared/Newsletter";
import useFetch from "./../hooks/useFetch";
import { BASE_URL } from "./../utils/config";
import { AuthContext } from "./../context/AuthContext";
import LoginModal from "../components/Login-Modal/LoginModal.jsx";
import { Oval } from "react-loader-spinner";
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import io from "socket.io-client";

const TourDetails = () => {
  const { id } = useParams();
  const reviewMsgRef = useRef("");
  const { user } = useContext(AuthContext);

  // fetch data from database
  const { data: tour, loading, error } = useFetch(`${BASE_URL}/tours/${id}`);

  // destructure properties from tour object
  const {
    photo,
    title,
    desc,
    price,
    address,
    city,
    distance,
    maxGroupSize,
  } = tour || {};

  console.log(tour); // Add this line to check if reviews are included in the tour

  // destructure reviews from tour object
  const initialReviews = tour ? tour.reviews : [];

  const { totalRating, avgRating } = calculateAvgRating(initialReviews);

  const [selectedRating, setSelectedRating] = useState(null);

  // Define el estado para las reseñas.
  const [reviews, setReviews] = useState(initialReviews);

  useEffect(() => {
    // Conéctate al servidor Socket.IO.
    const socket = io('http://localhost:4000');  // Cambia la URL por la de tu servidor.

    // Escucha el evento 'new review'.
    socket.on('new review', (newReview) => {
      // Asegúrate de que la nueva reseña pertenezca a este tour.
      if (newReview.tourId === id) {
        // Cuando se recibe un nuevo evento de reseña, actualiza el estado de las reseñas.
        setReviews((prevReviews) => [...prevReviews, newReview]);
      }
    });
    // Esto imprimirá las reseñas en la consola cada vez que se actualicen.
    console.log(reviews);

    // Cuando se desmonte el componente, desconecta el socket.
    return () => {
      socket.disconnect();
    };
  }, [id, reviews]); // Asegúrate de incluir `reviews` en las dependencias del useEffect.

  //Modal 
  const [isModalOpen, setIsModalOpen] = useState(false);
  const toggleModal = () => setIsModalOpen(!isModalOpen);
  const handleOpenModal = (e) => {
    if (!user) {
      setIsModalOpen(true);
    }
  };

  // submit request to the server
  const submitHandler = async e => {
    e.preventDefault();
    const reviewText = reviewMsgRef.current.value;

    console.log('submitHandler invoked'); // Add this

    try {
      console.log('User object:', user); // Add this

      if (!user || user === undefined || user === null) {
        return handleOpenModal();
      }

      const reviewObj = {
        username: user?.username,
        reviewText,
        rating: selectedRating,
      };

      console.log('Review Object:', reviewObj); // Add this
      console.log('Request URL:', `${BASE_URL}/review/${id}`); // Add this

      const res = await fetch(`${BASE_URL}/review/${id}`, {
        method: "post",
        headers: {
          "content-type": "application/json",
        },
        credentials: "include",
        body: JSON.stringify(reviewObj),
      });

      const result = await res.json();
      if (!res.ok) {
        return toast.error(result.message);
      }

    } catch (err) {
      toast.error(err.message);
    }
  };

  useEffect(() => {
    window.scrollTo(0, 0);
  }, [tour]);

  return (
    <>
      <section>
        <Container>
          {loading &&
            <h4 className="text-center pt-5">
              <Oval
                color="#f11c00"
                height={100}
                width={100}
                timeout={3000} //3 secs
              />
            </h4>
          }
          {error && <h4 className="text-center pt-5">{error}</h4>}
          {!loading && !error && (
            <Row>
              <Col lg="8">
                <div className="tour__content">
                  <img src={photo} alt="" />

                  <div className="tour__info">
                    <h2>{title}</h2>

                    <div className="d-flex align-items-center gap-5">
                      <span className="tour__rating d-flex align-items-center gap-1">
                        <i
                          className="ri-star-s-fill"
                          style={{ color: "var(--secondary-color)" }}
                        ></i>
                        {avgRating === 0 ? null : avgRating}
                        {totalRating === 0 ? (
                          "Not rated"
                        ) : (
                          <span>({reviews?.length})</span>
                        )}
                      </span>
                      <span>
                        <i className="ri-map-pin-user-fill"></i> {address}
                      </span>
                    </div>
                    <div className="tour__extra-details">
                      <span>
                        <i className="ri-map-pin-2-line"></i> {city}
                      </span>
                      <span>
                        <i className="ri-money-dollar-circle-line"></i> S/.{price}{" "}
                        /por person
                      </span>
                      <span>
                        <i className="ri-map-pin-time-line"></i> {distance} k/m
                      </span>
                      <span>
                        <i className="ri-group-line"></i> {maxGroupSize} personas
                      </span>
                    </div>
                    <h5>Descripcion</h5>
                    <p>{desc}</p>
                  </div>

                  {/* ========== tour reviews section =========== */}
                  <div className="tour__reviews mt-4">
                    <h4>Reviews ({reviews?.length} reviews)</h4>

                    <Form onSubmit={submitHandler}>
                      <div className="d-flex align-items-center gap-3 mb-4 rating__group">
                        <span onClick={() => setSelectedRating(1)}>1
                          <i
                            className="ri-star-s-fill"
                            style={{
                              color: selectedRating >= 1 ? "var(--secondary-color)" : "black",
                            }}
                          ></i>
                        </span>
                        <span onClick={() => setSelectedRating(2)}>2
                          <i
                            className="ri-star-s-fill"
                            style={{
                              color: selectedRating >= 2 ? "var(--secondary-color)" : "black",
                            }}
                          ></i>
                        </span>
                        <span onClick={() => setSelectedRating(3)}>3
                          <i
                            className="ri-star-s-fill"
                            style={{
                              color: selectedRating >= 3 ? "var(--secondary-color)" : "black",
                            }}
                          ></i>
                        </span>
                        <span onClick={() => setSelectedRating(4)}>4
                          <i
                            className="ri-star-s-fill"
                            style={{
                              color: selectedRating >= 4 ? "var(--secondary-color)" : "black",
                            }}
                          ></i>
                        </span>
                        <span onClick={() => setSelectedRating(5)}>5
                          <i
                            className="ri-star-s-fill"
                            style={{
                              color: selectedRating >= 5 ? "var(--secondary-color)" : "black",
                            }}
                          ></i>
                        </span>
                      </div>

                      <div className="review__input">
                        <input
                          type="text"
                          ref={reviewMsgRef}
                          placeholder="Danos tu opinion"
                          required
                        />
                        <button
                          className="btn primary__btn text-white"
                          type="submit"
                        >
                          Enviar
                        </button>
                      </div>
                    </Form>

                    <ListGroup className="user__reviews">
                      {reviews?.map((review, index) => {
                          console.log("Review: ", review); // Esto imprimirá cada reseña individualmente.
                        <div className="review__item" key={index}>
                          <img src={avatar} alt="" />

                          <div className="w-100">
                            <div className="d-flex align-items-center justify-content-between">
                              <div>
                                <h5>{review.username}</h5>
                                <p>
                                  {new Date(review.createdAt).toLocaleDateString("es-PE")
                                  }
                                </p>
                              </div>
                              <span className="d-flex align-items-center">
                                {review.rating}
                                <i className="ri-star-s-fill"></i>
                              </span>
                            </div>

                            <h6>{review.reviewText}</h6>
                          </div>
                        </div>
                      })}
                    </ListGroup>
                  </div>
                  {/* ========== tour reviews section end =========== */}

                </div>
              </Col>
              <Col lg="4">
                <Booking tour={tour} avgRating={avgRating} />
              </Col>
            </Row>
          )}
        </Container>
      </section>
      {/*============== Modal ====================*/}
      <LoginModal
        isModalOpen={isModalOpen}
        toggleModal={toggleModal}
      />
      {/*============== Modal end ====================*/}
      <Newsletter />
    </>
  );
};

export default TourDetails;
