import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BASE_URL } from "../../utils/config.js";

const EditTour = ({ match }) => {
    const [tourData, setTourData] = useState({
        title: '',
        city: '',
        address: '',
        distance: '',
        photo: '',
        desc: '',
        reviews: [],
        price: '',
        maxGroupSize: '',
        featured: false,
    });

    useEffect(() => {
        const fetchTour = async () => {
            const res = await axios.get(`${BASE_URL}/tour/${match.params._id}`);
            setTourData(res.data);
        };
        fetchTour();
    }, [match.params._id]);

    const handleChange = (event) => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        setTourData({
            ...tourData,
            [name]: value,
        });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            await axios.put(`${BASE_URL}/tour/${match.params._id}`, tourData);
            alert('Tour actualizado exitosamente!');
        } catch (error) {
            alert('Ocurrió un error al actualizar el tour');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                name="title"
                value={tourData.title}
                onChange={handleChange}
                required
            />
            <input
                type="text"
                name="city"
                value={tourData.city}
                onChange={handleChange}
                required
            />
            <input
                type="text"
                name="address"
                value={tourData.address}
                onChange={handleChange}
                required
            />
            <input
                type="number"
                name="distance"
                value={tourData.distance}
                onChange={handleChange}
                required
            />
            <input
                type="text"
                name="photo"
                value={tourData.photo}
                onChange={handleChange}
                required
            />
            <textarea
                name="desc"
                value={tourData.desc}
                onChange={handleChange}
                required
            />
            <input
                type="number"
                name="price"
                value={tourData.price}
                onChange={handleChange}
                required
            />
            <input
                type="number"
                name="maxGroupSize"
                value={tourData.maxGroupSize}
                onChange={handleChange}
                required
            />
            <label>
                Featured:
                <input
                    name="featured"
                    type="checkbox"
                    checked={tourData.featured}
                    onChange={handleChange}
                />
            </label>
            <input type="submit" value="Actualizar tour" />
        </form>
    );
};

export default EditTour;
