import React, { useState, useEffect } from "react";
import { Button } from "reactstrap";
import { BASE_URL } from "../../utils/config";

import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Payment = ({ tour, quantity, totalPrice, booking, guestDnis, user, handleOpenModal }) => {
  const [initPoint, setInitPoint] = useState(null);

  useEffect(() => {
    const createPaymentPreference = async () => {
      try {
        const response = await fetch(`${BASE_URL}/mercadopago/create_payment`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            product: {
              id: tour.id,
              title: tour.title, // Asegúrate de que el título del tour se pase aquí
              unit_price: tour.price
            },
            quantity: parseInt(quantity, 10),
            totalPrice
          }),
        });

        if (!response.ok) {
          throw new Error("Error al crear la preferencia de pago de MercadoPago");
        }

        const data = await response.json();
        setInitPoint(data.init_point);
      } catch (error) {
        console.error(error.message);
      }
    };

    createPaymentPreference();
  }, [tour, quantity, totalPrice]);

  const handlePayment = async () => {
    try {
      if (!user || user === undefined || user === null) {
        return handleOpenModal(); // Abre el modal de inicio de sesión si el usuario no ha iniciado sesión
      }

      // Verifica si todos los campos requeridos están llenos
      if (
        !booking.phone ||
        !booking.bookAt ||
        !booking.guestSize ||
        guestDnis.some(dni => !dni) // Verifica si algún DNI está vacío
      ) {
        return toast.error("Please fill in all required fields.");
      }

      // Guarda la información de reserva en la base de datos aquí

      if (initPoint) {
        window.open(initPoint, "_blank");
      }

    } catch (err) {
      toast.error(err.message);
    }
  };



  return (
    <div className="payment">
      <Button
        className="btn primary__btn w-100 mt-4"
        onClick={handlePayment}
        disabled={!initPoint}
      >
        Pagar con MercadoPago
      </Button>
    </div>
  );
};

export default Payment;
