import React, { useState, useContext, useEffect } from "react";
import "./booking.css";
import Payment from "../Payment/Payment.jsx";
import {
    Form,
    FormGroup,
    ListGroup,
    ListGroupItem,
} from "reactstrap";
import LoginModal from "../Login-Modal/LoginModal.jsx";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import { BASE_URL } from "../../utils/config";

import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Booking = ({ tour, avgRating }) => {
    const { price, reviews, title } = tour;
    const navigate = useNavigate();

    const { user } = useContext(AuthContext);

    const [booking, setBooking] = useState({
        userId: user && user._id,
        userEmail: user && user.email,
        tourName: title,
        phone: "",
        guestSize: 1,
        booAt: "",
    });

    const serviceFee = 10;
    const totalAmount =
        Number(price) * Number(booking.guestSize) + Number(serviceFee);

    const [guestDnis, setGuestDnis] = useState([]);

    useEffect(() => {
        setGuestDnis(new Array(booking.guestSize).fill(''));
    }, [booking.guestSize]);


    // DNI Token - debes mantenerlo en secreto en un archivo .env o en una variable de entorno
const DNI_TOKEN = 'eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJlbWFpbCI6ImtldmluZmdlMjQxMEBnbWFpbC5jb20ifQ.Ha6IN3AcX5zAC1pBssILUz1U8lg03rodOkh2rpxjKag';

async function fetchDniData(dni) {
    try {
        const response = await fetch(
            `https://dniruc.apisperu.com/api/v1/dni/${dni}?token=${DNI_TOKEN}`
        );

        const data = await response.json();

        if (data && data.success) {
            return data;
        } else {
            console.error('Datos no disponibles:', data);
            // Lanzar un error para manejarlo en la función handleDniChange
            throw new Error('No se pudo obtener los datos. Por favor, verifique el DNI e intente nuevamente.');
        }
    } catch (error) {
        console.error('Error al obtener datos:', error);
        throw error;
    }
}

const handleDniChange = (index) => async (e) => {
    const newDni = e.target.value;
    const newGuestDnis = [...guestDnis];
    newGuestDnis[index] = newDni;
    setGuestDnis(newGuestDnis);

    try {
        const data = await fetchDniData(newDni);
        // Ahora puedes utilizar los datos devueltos por la API de RENIEC.
        // Por ejemplo, puedes mostrar el nombre y los apellidos en la interfaz de usuario.
        console.log(data.nombres, data.apellidoPaterno, data.apellidoMaterno);
    } catch (error) {
        // Muestra algún tipo de mensaje de error en la interfaz de usuario
        console.error(error);
    }
};

    



    const { dispatch } = useContext(AuthContext);

    const [credentials, setCredentials] = useState({
        email: undefined,
        password: undefined,
    });

    const quantity = booking.guestSize;

    const handleOpenModal = () => {
        setIsModalOpen(true);
    };

    const currentURL = window.location.pathname;
    const [isModalOpen, setIsModalOpen] = useState(false);
    const toggleModal = () => setIsModalOpen(!isModalOpen);

    const handleChange = (e) => {
        const value = e.target.id === 'guestSize' ? parseInt(e.target.value, 10) : e.target.value;
        setBooking((prev) => ({ ...prev, [e.target.id]: value }));
    };
    
    const handleLogin = async (e) => {
        e.preventDefault();

        try {
            const res = await fetch(`${BASE_URL}/auth/login`, {
                method: "post",
                headers: {
                    "content-type": "application/json",
                },
                credentials: "include",
                body: JSON.stringify(credentials),
            });

            const result = await res.json();

            if (!res.ok) toast.error(result.message);

            console.log(result.data);

            dispatch({ type: "LOGIN_SUCCESS", payload: result.data });
            setIsModalOpen(false); // Cierra el modal antes de redirigir al usuario
            navigate(currentURL); // Redirige al usuario a la página del tour actual en lugar de la página de inicio
        } catch (err) {
            dispatch({ type: "LOGIN_FAILURE", payload: err.message });
        }
    };


    return (
        <div className="booking">
            <div className="booking__top d-flex align-items-center justify-content-between">
                <h3>
                    S/.{price} <span>/por persona</span>
                </h3>

                <span className="tour__rating d-flex align-items-center">
                    <i className="ri-star-s-fill"></i>
                    {avgRating === 0 ? null : avgRating} ({reviews?.length})
                </span>
            </div>

            {/*============== booking form ====================*/}
            <div className="booking__form">
                <h5>Informacion</h5>
                <Form className="booking__info-form" onSubmit={(e) => e.preventDefault()}>
                    <FormGroup className="d-flex align-items-center gap-3">
                        <input
                            type="date"
                            placeholder=""
                            id="bookAt"
                            required
                            onChange={handleChange}
                        />
                        <input
                            type="number"
                            placeholder="Guest"
                            id="guestSize"
                            required
                            onChange={handleChange}
                        />
                    </FormGroup>
                    <FormGroup>
                        <input
                            type="number"
                            placeholder="Telefono"
                            id="phone"
                            required
                            onChange={handleChange}
                        />
                    </FormGroup>
                    {guestDnis.map((dni, index) => (
                        <FormGroup key={index}>
                            <input
                                type="text"
                                placeholder={`DNI del invitado ${index + 1}`}
                                required
                                value={dni}
                                onChange={handleDniChange(index)}
                            />
                        </FormGroup>
                    ))}
                </Form>
            </div>
            {/*============== booking end ====================*/}

            {/*============== booking botton ====================*/}
            <div className="booking__botton">
                <ListGroup>
                    <ListGroupItem className="border-0 px-0">
                        <h5 className="d-flex align-items-center gap-1">
                            S/.{price} <i className="ri-close-line"></i> 1 persona
                        </h5>
                        <span>S/.{price}</span>
                    </ListGroupItem>
                </ListGroup>
                <ListGroup>
                    <ListGroupItem className="border-0 px-0">
                        <h5>Cargo por servicio</h5>
                        <span>S/.{serviceFee}</span>
                    </ListGroupItem>
                </ListGroup>
                <ListGroup>
                    <ListGroupItem className="border-0 px-0 total">
                        <h5>Total</h5>
                        <span>S/.{totalAmount}</span>
                    </ListGroupItem>
                </ListGroup>

                {/* Componente de pago de MercadoPago */}
                <Payment
                    tour={tour}
                    quantity={quantity}
                    totalPrice={totalAmount}
                    booking={booking}
                    guestDnis={guestDnis}
                    user={user}
                    handleOpenModal={handleOpenModal}
                />

            </div>
            {/*============== booking botton  end ====================*/}

            {/*============== Modal ====================*/}
            <LoginModal
                isModalOpen={isModalOpen}
                toggleModal={toggleModal}
                handleLogin={handleLogin}
                credentials={credentials}
                setCredentials={setCredentials}
            />
            {/*============== Modal end ====================*/}
        </div>
    );
};

export default Booking;
