import React, { useState, useContext, useEffect } from "react";
import "./booking.css";
import Payment from "../Payment/Payment.jsx";
import DniField from '../DNI/DniField.jsx';
import LoginModal from "../Login-Modal/LoginModal.jsx";
import { Form, FormGroup, ListGroup, ListGroupItem } from "reactstrap";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import { BASE_URL } from "../../utils/config";
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Booking = ({ tour, avgRating }) => {
    const { price, reviews, title } = tour;
    const navigate = useNavigate();

    const { user } = useContext(AuthContext);
    const currentURL = window.location.pathname;

    const [booking, setBooking] = useState({
        userId: user && user._id,
        userEmail: user && user.email,
        tourName: title,
        phone: "",
        guestSize: "",
        booAt: "",
    });

    const serviceFee = 10;
    const totalAmount =
        Number(price) * Number(booking.guestSize) + Number(serviceFee);

    const { dispatch } = useContext(AuthContext);

    const [credentials, setCredentials] = useState({
        email: undefined,
        password: undefined,
    });

    const handleOpenModal = () => {
        setIsModalOpen(true);
    };

    const [isModalOpen, setIsModalOpen] = useState(false);
    const toggleModal = () => setIsModalOpen(!isModalOpen);

    const handleChange = (e) => {
        const value = e.target.id === 'guestSize' ? Number(e.target.value) : e.target.value;
        setBooking((prev) => ({ ...prev, [e.target.id]: value }));
    };

    // Declaramos 'quantity' antes de su uso.
    const [quantity, setQuantity] = useState(booking.guestSize);

    // Ahora podemos utilizar 'quantity' sin problemas.
    const [dni, setDni] = useState(new Array(quantity).fill(""));
    const [userData, setUserData] = useState(new Array(quantity).fill({}));

    useEffect(() => {
        // Si el número de invitados cambia, reiniciar los valores de DNI y userData
        setDni(new Array(booking.guestSize).fill(""));
        setUserData(new Array(booking.guestSize).fill({}));
        setQuantity(booking.guestSize);
    }, [booking.guestSize]);

    const handleLogin = async (e) => {
        e.preventDefault();

        try {
            const res = await fetch(`${BASE_URL}/auth/login`, {
                method: "post",
                headers: {
                    "content-type": "application/json",
                },
                credentials: "include",
                body: JSON.stringify(credentials),
            });

            const result = await res.json();

            if (!res.ok) {
                // Aquí puedes manejar los errores a nivel de la aplicación
                // Por ejemplo, si el servidor responde con un mensaje de error
                toast.error(result.message);
            } else {
                // Si la respuesta es exitosa, puedes proceder como antes
                dispatch({ type: "LOGIN_SUCCESS", payload: result.data });
                setIsModalOpen(false); // Cierra el modal antes de redirigir al usuario
                navigate(currentURL); // Redirige al usuario a la página del tour actual en lugar de la página de inicio
            }
        } catch (err) {
            // Aquí puedes manejar los errores a nivel de red
            dispatch({ type: "LOGIN_FAILURE", payload: err.message });
            toast.error("Ha ocurrido un error al iniciar sesión. Por favor, inténtalo de nuevo.");
        }
    };

    return (
        <div className="booking">
            <div className="booking__top d-flex align-items-center justify-content-between">
                <h3>
                    S/.{price} <span>/por persona</span>
                </h3>

                <span className="tour__rating d-flex align-items-center">
                    <i className="ri-star-s-fill"></i>
                    {avgRating === 0 ? null : avgRating} ({reviews?.length})
                </span>
            </div>

            {/*============== booking form ====================*/}
            <div className="booking__form">
                <h5>Informacion</h5>
                <Form className="booking__info-form" onSubmit={(e) => e.preventDefault()}>
                    <FormGroup>
                        <input
                            type="number"
                            placeholder="N° de Personas"
                            id="guestSize"
                            required
                            value={booking.guestSize} // Asegúrate de que el valor del campo de entrada esté sincronizado con booking.guestSize
                            onChange={handleChange}
                            min="0"

                        />
                    </FormGroup>
                    <FormGroup>
                        <input
                            type="number"
                            placeholder="Telefono"
                            id="phone"
                            required
                            onChange={handleChange}
                            min="0"
                        />
                    </FormGroup>
                    <FormGroup className="d-flex align-items-center gap-3">
                        <input
                            type="date"
                            placeholder=""
                            id="bookAt"
                            required
                            onChange={handleChange}
                        />
                    </FormGroup>

                    {/*Agregar campos de DNI*/}
                    {quantity > 0 && [...Array(quantity)].map((_, i) => (
                        <DniField
                            key={i}
                            index={i}
                            dni={dni[i]}
                            setDni={setDni}
                            userData={userData[i]}
                            setUserData={(newData) => {
                                let tempUserData = [...userData];
                                tempUserData[i] = newData;
                                setUserData(tempUserData);
                            }}
                        />
                    ))}

                </Form>
            </div>
            {/*============== booking end ====================*/}

            {/*============== booking botton ====================*/}
            <div className="booking__botton">
                <ListGroup>
                    <ListGroupItem className="border-0 px-0">
                        <h5 className="d-flex align-items-center gap-1">
                            S/.{price} <i className="ri-close-line"></i> 1 persona
                        </h5>
                        <span>S/.{price}</span>
                    </ListGroupItem>
                </ListGroup>
                <ListGroup>
                    <ListGroupItem className="border-0 px-0">
                        <h5>Cargo por servicio</h5>
                        <span>S/.{serviceFee}</span>
                    </ListGroupItem>
                </ListGroup>
                <ListGroup>
                    <ListGroupItem className="border-0 px-0 total">
                        <h5>Total</h5>
                        <span>S/.{totalAmount}</span>
                    </ListGroupItem>
                </ListGroup>

                {/* Componente de pago de MercadoPago */}
                <Payment
                    tour={tour}
                    quantity={quantity}
                    totalPrice={totalAmount}
                    booking={booking}
                    user={user}
                    handleOpenModal={handleOpenModal}
                />
            </div>
            {/*============== booking botton  end ====================*/}

            {/*============== Modal ====================*/}
            <LoginModal
                isModalOpen={isModalOpen}
                toggleModal={toggleModal}
                handleLogin={handleLogin}
                credentials={credentials}
                setCredentials={setCredentials}
            />
            {/*============== Modal end ====================*/}
        </div>
    );
};

export default Booking;
