import React, { useState, useEffect } from "react";
import { Button } from "reactstrap";
import { BASE_URL } from "../../utils/config";

const Payment = ({ tour, quantity, totalPrice }) => {
  const [initPoint, setInitPoint] = useState(null);

  useEffect(() => {
    const createPaymentPreference = async () => {
        try {
          const response = await fetch(`${BASE_URL}/mercadopago/create_payment`, {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({ 
                product: { 
                  id: tour.id, 
                  title: tour.tourName, // Asegúrate de que el título del tour se pase aquí
                  unit_price: tour.price 
                }, 
                quantity: parseInt(quantity, 10),
                totalPrice 
              }),
          });
      
          if (!response.ok) {
            throw new Error("Error al crear la preferencia de pago de MercadoPago");
          }
      
          const data = await response.json();
          setInitPoint(data.init_point);
        } catch (error) {
          console.error(error.message);
        }
      };

    createPaymentPreference();
  }, [tour, quantity, totalPrice]);

  const handlePayment = () => {
    if (initPoint) {
      window.open(initPoint, "_blank");
    }
  };

  return (
    <div className="payment">
      <Button
        className="btn primary__btn w-100 mt-4"
        onClick={handlePayment}
        disabled={!initPoint}
      >
        Pagar con MercadoPago
      </Button>
    </div>
  );
};

export default Payment;
