import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

const ResetPassword = () => {
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const { token } = useParams();  // Obtén el token de la URL
    const [error, setError] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [showConfirmPassword, setShowConfirmPassword] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (password !== confirmPassword) {
            setError('Las contraseñas no coinciden');
            return;
        }

        try {
            const response = await fetch('/api/reset-password', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ password, token })  // Pasa el token junto con la contraseña
            });

            if (!response.ok) {
                throw new Error('Error al restablecer la contraseña');
            }

            const data = await response.json();

            // La contraseña se restableció correctamente
            // Puedes redirigir al usuario a la página de inicio de sesión aquí, si quieres
        } catch (error) {
            // Aquí es donde manejas los errores
            console.error(error);
        }
    };

    const toggleShowPassword = () => {
        setShowPassword(!showPassword);
    };

    const toggleShowConfirmPassword = () => {
        setShowConfirmPassword(!showConfirmPassword);
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Nueva contraseña:
                <input
                    type={showPassword ? 'text' : 'password'}
                    value={password}
                    onChange={e => setPassword(e.target.value)}
                    required
                />
                <button type="button" onClick={toggleShowPassword}>
                    Mostrar {showPassword ? 'ocultar' : 'mostrar'}
                </button>
            </label>
            <label>
                Confirmar contraseña:
                <input
                    type={showConfirmPassword ? 'text' : 'password'}
                    value={confirmPassword}
                    onChange={e => setConfirmPassword(e.target.value)}
                    required
                />
                <button type="button" onClick={toggleShowConfirmPassword}>
                    Mostrar {showConfirmPassword ? 'ocultar' : 'mostrar'}
                </button>
            </label>
            {error && <p className="error-message">{error}</p>}
            <button type="submit">Restablecer contraseña</button>
        </form>
    );
};

export default ResetPassword;
