import React, { useState, useContext } from "react";
import "./booking.css";
import {
    Form,
    FormGroup,
    ListGroup,
    ListGroupItem,
    Button,
    Modal,
    ModalBody,
} from "reactstrap";

import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import { BASE_URL } from "../../utils/config";

const Booking = ({ tour, avgRating }) => {
    const { price, reviews, title } = tour;
    const navigate = useNavigate();

    const { user } = useContext(AuthContext);

    const [isModalOpen, setIsModalOpen] = useState(false);

    const [credentials, setCredentials] = useState({
        email: "",
        password: "",
    });

    const [booking, setBooking] = useState({
        userId: user && user._id,
        userEmail: user && user.email,
        tourName: title,
        fulName: "",
        phone: "",
        guestSize: 1,
        booAt: "",
    });

    const handleChange = (e) => {
        setBooking((prev) => ({ ...prev, [e.target.id]: e.target.value }));
    };

    const serviceFee = 10;
    const totalAmount =
        Number(price) * Number(booking.guestSize) + Number(serviceFee);

        const handleOpenModal = () => {
            setIsModalOpen(true);
          };

    //send data to the server

    const handleClik = async (e) => {
        e.preventDefault();
      
        console.log(booking);
      
        try {
          if (!user || user === undefined || user === null) {
            return handleOpenModal();
          }
      
          // ...
        } catch (err) {
          alert(err.message);
        }
      };

      

    return (
        <div className="booking">
            <div className="booking__top d-flex align-items-center justify-content-between">
                <h3>
                    S/.{price} <span>/por persona</span>
                </h3>

                <span className="tour__rating d-flex align-items-center">
                    <i class="ri-star-s-fill"></i>
                    {avgRating === 0 ? null : avgRating} ({reviews?.length})
                </span>
            </div>

            {/*============== booking form ====================*/}
            <div className="booking__form">
                <h5>Informacion</h5>
                <Form className="booking__info-form" onSubmit={handleClik}>
                    <FormGroup>
                        <input
                            type="text"
                            placeholder="Nombre Completo"
                            id="fullName"
                            required
                            onChange={handleChange}
                        />
                    </FormGroup>
                    <FormGroup>
                        <input
                            type="number"
                            placeholder="Telefono"
                            id="phone"
                            required
                            onChange={handleChange}
                        />
                    </FormGroup>
                    <FormGroup className="d-flex align-items-center gap-3">
                        <input
                            type="date"
                            placeholder=""
                            id="bookAt"
                            required
                            onChange={handleChange}
                        />
                        <input
                            type="number"
                            placeholder="Guest"
                            id="guestSize"
                            required
                            onChange={handleChange}
                        />
                    </FormGroup>
                </Form>
            </div>
            {/*============== booking end ====================*/}

            {/*============== Modal ====================*/}
      <Modal isOpen={modal} toggle={closeModal}>
        <ModalHeader>Iniciar Sesión</ModalHeader>
        <ModalBody>
          <Form onSubmit={handleLoginSubmit}>
            <FormGroup>
              <input
                type="email"
                placeholder="Correo Electronico"
                id="email"
                required
                onChange={handleLoginChange}
              />
            </FormGroup>
            <FormGroup>
              <input
                type="password"
                placeholder="Contraseña"
                id="password"
                required
                onChange={handleLoginChange}
              />
            </FormGroup>
            <Button className="btn primary__btn w-100" type="submit">
              Ingresar
            </Button>
          </Form>
        </ModalBody>
      </Modal>
      {/*============== Modal end ====================*/}

            {/*============== booking botton ====================*/}
            <div className="booking__botton">
                <ListGroup>
                    <ListGroupItem className="border-0 px-0">
                        <h5 className="d-flex align-items-center gap-1">
                            S/.{price} <i class="ri-close-line"></i> 1 persona
                        </h5>
                        <span>S/.{price}</span>
                    </ListGroupItem>
                </ListGroup>
                <ListGroup>
                    <ListGroupItem className="border-0 px-0">
                        <h5>Cargo por servicio</h5>
                        <span>S/.{serviceFee}</span>
                    </ListGroupItem>
                </ListGroup>
                <ListGroup>
                    <ListGroupItem className="border-0 px-0 total">
                        <h5>Total</h5>
                        <span>S/.{totalAmount}</span>
                    </ListGroupItem>
                </ListGroup>

                <Button className="btn primary__btn w-100 mt-4" onClick={handleOpenModal}>
                    Pagar
                </Button>
            </div>
        </div>
        
    );
    
};



export default Booking;
